using UnityEngine;
using UnityEngine.UI;
using System.Collections.Generic;
using LitJson;
using System.IO;
using System;

public class ScrollViewManager : MonoBehaviour
{
    public RectTransform content;
    public GameObject listItemPrefab;
    public float elementWidth = 300f;
    public float elementHeight = 200f;

    private List<string> imagePaths = new List<string>();
    private List<string> textList = new List<string>();

    void Start()
    {
        /************************************************************************************************************
         * json 파일에서 이미지 경로와 일기장 내용을 가져와서 스크롤뷰에 보여주는 코드를 구현하였습니다.
         * base64로 인코딩된 이미지 string을 디코딩하여 이미지 파일(png)로 만들어서 Application.persistentDataPath에
         저장한 뒤에 저장된 경로와 사용자가 입력한 text를 json 파일에 저장하는 것도 추가로 구현하였습니다. 해당코드
         보시고 api에서 데이터 받을 때 인코딩된 string을 디코딩해서 이미지 파일로 저장하고 해당 이미지 파일 경로와 
         사용자가 입력한 텍스트를 json 파일에 저장하시면 될 것 같습니다.
         * 참고: 런타임 도중에 json 파일이 변경이 되어야 했기 때문에 asset이 아닌 Application.persistentDataPath에 저장
         하였습니다.
         * 테스트시에 일기장 버튼 눌러주세요. 한번 실행시 윈도우 기준으로 C:\Users\<사용자이름>\AppData\LocalLow\<회사이
         름>\<앱이름>에 json 파일이 생성되고 dummy sample이미지들이 실행할 때마다 쌓이니 주의해주세요. 테스트만 해보시고
         json 파일과 png 파일을 해당 경로에서 삭제해주세요.
         ************************************************************************************************************/

        //동일한 이미지와 텍스트를 json 파일에 저장
        /* for (int i = 0; i < 4; i++)
        {
            // 샘플 이미지와 텍스트 데이터 생성
            Texture2D texture2D = Base64ToTexture2D("iVBORw0KGgoAAAANSUhEUgAAAgAAAAIACAYAAAD0eNT6AAAACXBIWXMAAA7DAAAOwwHHb6hkAAAAGXRFWHRTb2Z0d2FyZQB3d3cuaW5rc2NhcGUub3Jnm + 48GgAAIABJREFUeJzs3Xd8VVW2wPHfvemNBBISCBA60jtI74h0aQI2rIgV64xjA8dR0dFRn33UsYtdUVBRQXoRAaV3Qi8JENLLzb3vjyMCEiA5e597blnfz + d + 3nvKXns9hJx199l7bRBCCCGEEEIIIYQQQgghhBBCCCFEIHDYnYAQQkkscAHQGKgCxAMJf / zP + D9 + TR5QDGQBJUAGsBPYAWwHCrybshDCF0gBIIT / SAB6AT2AZhgP / toa4h4ANgNLgMV / fI5riCuEEEIIE0IwHviPAcsAF + ");
            string filename = $"sample_image_{i}.png"; // 이미지 파일명 설정
            string filePath = Application.persistentDataPath + '/' + filename;
            SaveTextureToFile(texture2D, filePath);
            string text = "샘플 일기 내용입니다. 실제로는 사용자의 일기 내용이 들어갑니다.";

            AddDataToJson(text, filePath);
        }*/

        string jsonFilePath = Application.persistentDataPath + "/diary.json";
        // diary.json이 있는지 확인
        // 파일이 존재하는지 확인
        if (!System.IO.File.Exists(jsonFilePath))
        {
            // 새로운 JSON 파일 생성
            using (StreamWriter writer = System.IO.File.CreateText(jsonFilePath))
            {
                writer.Write("[]"); // 빈 배열로 초기화
            }
        }
        LoadDataFromJSON(jsonFilePath); // JSON 파일에서 데이터 읽어오기

        int totalElements = Mathf.Min(textList.Count, imagePaths.Count);

        // 스크롤 뷰에 이미지와 텍스트 프리팹들 표시
        for (int i = 0; i < totalElements; i++)
        {
            GameObject listItem = Instantiate(listItemPrefab, content);
            RectTransform listItemRect = listItem.GetComponent<RectTransform>();

            RawImage imageField = listItem.GetComponentInChildren<RawImage>();
            RectTransform imageRect = imageField.GetComponent<RectTransform>();
            imageRect.sizeDelta = new Vector2(elementHeight, elementHeight);

            Text textField = listItem.GetComponentInChildren<Text>();
            RectTransform textRect = textField.GetComponent<RectTransform>();
            textRect.sizeDelta = new Vector2(elementWidth, elementHeight);

            listItemRect.sizeDelta = new Vector2(elementWidth, elementHeight);
            listItemRect.anchoredPosition = new Vector2(0, -i * elementHeight);

            textField.text = textList[i];

            Texture2D texture = LoadImage(imagePaths[i]);
            if (texture != null)
            {
                imageField.texture = texture;
            }
        }

        float contentHeight = totalElements * elementHeight;
        content.sizeDelta = new Vector2(content.sizeDelta.x, contentHeight);
    }

    void LoadDataFromJSON(string filePath)
    {
        if (System.IO.File.Exists(filePath))
        {
            string jsonString = System.IO.File.ReadAllText(filePath);
            JsonData jsonData = JsonMapper.ToObject(jsonString);

            for (int i = 0; i < jsonData.Count; i++)
            {
                string text = jsonData[i]["diarytext"].ToString();
                textList.Add(text);

                string imagePath = jsonData[i]["diaryimage"].ToString();
                imagePaths.Add(imagePath);
            }
        }
        else
        {
            Debug.LogError("JSON file not found at: " + filePath);
        }
    }

    Texture2D LoadImage(string filePath)
    {
        if (!System.IO.File.Exists(filePath))
        {
            Debug.LogError($"File does not exist at path: {filePath}");
            return null;
        }

        byte[] bytes = System.IO.File.ReadAllBytes(filePath); // 파일에서 바이트 배열 읽기

        Texture2D texture = new Texture2D(1, 1);
        if (!texture.LoadImage(bytes))
        {
            Debug.LogError("Error loading image");
            return null;
        }

        return texture;
    }

    // base64 string을 이미지로 저장
    Texture2D Base64ToTexture2D(string base64String)
    {
        byte[] bytes = System.Convert.FromBase64String(base64String);

        Texture2D texture = new Texture2D(1, 1);
        texture.LoadImage(bytes);

        return texture;
    }

    // 이미지를 파일에 저장
    void SaveTextureToFile(Texture2D texture, string filePath)
    {
        byte[] bytes = texture.EncodeToPNG(); // PNG로 인코딩된 바이트 배열 획득
        System.IO.File.WriteAllBytes(filePath, bytes); // 파일로 저장
    }

    // 기존 json 파일에 파일 경로와 이미지
    void AddDataToJson(string text, string imagePath)
    {
        string jsonFilePath = Application.persistentDataPath + "/diary.json";

        // 파일이 존재하는지 확인
        if (!System.IO.File.Exists(jsonFilePath))
        {
            // 새로운 JSON 파일 생성
            using (StreamWriter writer = System.IO.File.CreateText(jsonFilePath))
            {
                writer.Write("[]"); // 빈 배열로 초기화
            }
        }

        // 기존 데이터 로드
        string jsonString = System.IO.File.ReadAllText(jsonFilePath);
        JsonData jsonData = JsonMapper.ToObject(jsonString);

        // 새로운 데이터 생성
        JsonData newData = new JsonData();
        newData["diarytext"] = text;
        newData["diaryimage"] = imagePath;

        // JSON 데이터에 새로운 데이터 추가
        jsonData.Add(newData);

        // 새로운 JSON 파일로 저장
        string newJsonString = JsonMapper.ToJson(jsonData);
        System.IO.File.WriteAllText(jsonFilePath, newJsonString);
    }
}
